 /* 
  * function to hide menu on scroll down
  * or show menu on scroll up
  * 
  *  and make mobile menu run
  * Made By Pedro Augusto;
 */
 
 //scroll effect
 var prevScroll = window.pageYOffset;
 window.onscroll = ()=>{
   const header = document.getElementsByTagName('header')[0]
   var correctScroll = window.pageYOffset;
   if(prevScroll > correctScroll) header.style.top = "0px";
   else header.style.top = "-100px";
   prevScroll = correctScroll;
  }

 //set mobile menu
 window.addEventListener('load', ()=>{
   const menuToggle = document.querySelector('.menu-toggle');
   const navBarLinks = document.querySelector('.nav');
   const logo = document.querySelector('.logo');

   menuToggle.addEventListener('click', () =>{
      menuToggle.classList.toggle('active');
      logo.classList.toggle('active-logo');

      navBarLinks.classList.toggle('active');
      navBarLinks.addEventListener('click', () =>{
          setTimeout(() => {
             if(menuToggle.classList.contains('active')){//if true, remove class
               menuToggle.classList.remove('active');
             }
              navBarLinks.classList.remove('active');
              logo.style.display = "inline";
          }, 300);
      });//end navBar event 
   });//and event menuToggle
 });//end window event